import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-menu-item',
  templateUrl: './menu-item.component.html',
  styleUrls: ['./menu-item.component.css']
})
export class MenuItemComponent{

  @Input() title!: any;
  @Input() path!: any;


}
<a
  class="itemmenu"
  color="primary"
  [routerLink]="[path]"
  style="text-decoration: none"
  ><span>{{ title }}</span></a
>
