import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-generic-table',
  templateUrl: './generic-table.component.html',
  styleUrls: ['./generic-table.component.css']
})
export class GenericTableComponent implements OnInit {

  @Input() dataSource: any;
  // @Input() displayedColumns: string[] = [];
  @Input() tableOptions: any;


  ngOnInit(): void {
    this.setColumns();
  }

  displayDynamicColumns(): void{
    if(this.tableOptions.canModify){
      this.tableOptions.displayedColumns.push("modify");
    }
    if(this.tableOptions.canDelete) {
      this.tableOptions.displayedColumns.push("delete")
    }
  }

  setColumns(): void{
    this.tableOptions.displayedColumns.push("visualizza");
    this.displayDynamicColumns();
  }

}
<table mat-table [dataSource]="dataSource" class="mat-elevation-z8 my-table">

  <ng-container *ngFor="let column of tableOptions.displayedColumns">

    <ng-container matColumnDef="{{column}}">
      <th mat-header-cell *matHeaderCellDef> {{column | titlecase }} </th>
      <td mat-cell *matCellDef="let element">
        <ng-container [ngSwitch]="column">
          <ng-container *ngSwitchCase="'visualizza'">
            <mat-icon>visibility</mat-icon>
          </ng-container>
          <ng-container *ngSwitchCase="'modify'">
            <mat-icon>edit</mat-icon>
          </ng-container>
          <ng-container *ngSwitchCase="'delete'">
            <mat-icon>delete_outline</mat-icon>
          </ng-container>
          <ng-container *ngSwitchDefault>
            {{element[column]}}
          </ng-container>
        </ng-container>
      </td>
    </ng-container>

  </ng-container>


  <tr mat-header-row *matHeaderRowDef="tableOptions.displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: tableOptions.displayedColumns;"></tr>

</table>
