import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css'],
})
export class HeaderComponent implements OnInit{
  ngOnInit(): void {
    this.userinitials = this.getInitials(localStorage.getItem("userFullName")||'')
  }
  userinitials:string="SS";
  getInitials(userFullName:string){
    let rgx = new RegExp(/(\p{L}{1})\p{L}+/, 'gu');
    let initials = [...userFullName.matchAll(rgx)] || [];
    return ((initials.shift()?.[1] || '') + (initials.pop()?.[1] || ''));
  }
}
<mat-toolbar class="toolbar" color="primary">
  <span id="logospan">
    <img class="logoToolbar" style="height: fit-content;" src="assets/img/bcsoft.png">
  </span>
  <span class="flex-spacer-navbutton"></span>
  <span>
    <app-navmenu></app-navmenu>
  </span>
  <span class="flex-spacer"></span>
  <span>
    <app-avatar [initials]="userinitials"></app-avatar>
  </span>
</mat-toolbar>
